css:
body {
    font-family: 'Segoe UI', Tahoma, sans-serif;
    background-color: #f4f6f9;
    padding: 40px;
    margin: 0;
}

.invoice-container {
    max-width: 800px;
    margin: auto;
    background: #ffffff;
    padding: 40px;
    border-radius: 10px;
    box-shadow: 0 8px 25px rgba(0,0,0,0.08);
}

.invoice-header {
    text-align: center;
    margin-bottom: 30px;
}

.invoice-header h2 {
    margin: 0;
    color: #1f3c88;
    letter-spacing: 1px;
}

.divider {
    height: 2px;
    background: #1f3c88;
    width: 60px;
    margin: 15px auto 0;
}

.invoice-info {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 15px 40px;
    margin-bottom: 30px;
}

.info-item {
    font-size: 14px;
    color: #444;
}

.info-item span {
    font-weight: 600;
    color: #222;
}

.amount-summary {
    margin-top: 20px;
}

table {
    width: 100%;
    border-collapse: collapse;
}

thead {
    background-color: #1f3c88;
    color: white;
}

th, td {
    padding: 12px;
    font-size: 14px;
}

th {
    text-align: left;
}

tbody tr {
    border-bottom: 1px solid #e0e0e0;
}

tbody tr:last-child {
    border-bottom: none;
}

.text-right {
    text-align: right;
}

.total-row {
    background-color: #f0f3fa;
    font-weight: bold;
}

.highlight {
    color: #1f3c88;
    font-weight: bold;
}

.footer {
    text-align: center;
    margin-top: 40px;
    font-size: 13px;
    color: #777;
}
